import logo from './logo.svg';
import './App.css';

import React, { useState, useEffect, Component } from "react";
import { Routes,Route } from 'react-router-dom'

import todos from "./apis";
import Form from "./components/Form";
import Section from "./components/Section";
import List from "./components/List";

// login components
import Signup from './components/sign-up'
import LoginForm from './components/login-form'
import Navbar from './components/navbar'
import Home from './components/home'
import Member from './components/member'


const appTitle = "My Todo List";


const App = () => {

componentDidMount = () => {
  this.getUser()
}

updateUser = (userObject) => {
  this.setState(userObject)
}

getUser = () => {
  axios.get('/user/').then(response => {
    console.log('Get user response: ')
    console.log(response.data)
    if (response.data.user) {
      console.log('Get User: There is a user saved in the server session: ')

      this.setState({
        loggedIn: true,
        username: response.data.user.username
      })
    } else {
      console.log('Get user: no user');
      this.setState({
        loggedIn: false,
        username: null
      })
    }
  })
}

redirectURL = (updatedValue) => {
  this.setState({redirect: updatedValue})
}

// to do

const [todoList, setTodoList] = useState([]);

    useEffect(() => {
        async function fetchData() {
            const { data } = await todos.get("/todos");
            setTodoList(data);
        }

        fetchData();
    }, []);

    const addTodo = async (item) => {
        const { data } = await todos.post("/todos", item);
        setTodoList((oldList) => [...oldList, data]);
    };

    const removeTodo = async (id) => {
        await todos.delete(`/todos/${id}`);
        setTodoList((oldList) => oldList.filter((item) => item._id !== id));
    };

    const editTodo = async (id, item) => {
        await todos.put(`/todos/${id}`, item);
    };

    return (
      <><div>

      <Navbar 
          updateUser={this.updateUser} 
          loggedIn={this.state.loggedIn} 
          redirect={this.state.redirect} 
          redirectURL={this.redirectURL} 
        />
        {/* greet user if logged in: */}
        {this.state.loggedIn &&
          <p className="mt-5 mb-4">Welcome back, {this.state.username}!</p>
        }
        {/* Routes to different components */}
        <Routes>
          <Route
            exact path="/"
            element={<Home />} />
          <Route
            path="/login"
            element={
              <LoginForm
                updateUser={this.updateUser}
              />}
          />
          <Route
            path="/signup"
            element={<Signup />}
          />
          <Route
            path="/member"
            element={<Member redirect={this.state.redirect} />} />
        </Routes>
      </div>
    );
  }

      <div className="container">
          <Section>
            <div class="heading">
              <img class="heading__img" src="https://s3-us-west-2.amazonaws.com/s.cdpn.io/756881/laptop.svg" />
              <h1 class="heading__title">{appTitle}</h1>
            </div>
          </Section>

          <Section>
            <Form addTodo={addTodo} />
          </Section>

          <Section>
            <List
              editTodoListProp={editTodo}
              removeTodoListProp={removeTodo}
              list={todoList} />
          </Section>
        </div></>
    );
};


export default App;
